import { useState, useEffect } from 'react';
import '../css/Weather.css';

export const Weather = () => {
    const [city, setCity] = useState('Seoul');
    const [weather, setWeather] = useState(null);

    useEffect(() => {
        if (city) {
            fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=163c2c5f76b523d8d2147930060e9bb9&units=metric&lang=kr`)
                .then(response => response.json())
                .then(data => setWeather(data));
        }
    }, [city]);

    return (
        <>
            <div className="MainWrap">
                <h1 className="Title">날씨정보페이지</h1>
                {weather && (
                    <div>
                        <h2>{weather.name}</h2>
                        <p>{weather.weather[0].description}</p>
                        <img src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`} alt={weather.weather[0].description} />
                        <p>온도: {weather.main.temp}°C</p>
                        <p>체감 온도: {weather.main.feels_like}°C</p>
                        <p>습도: {weather.main.humidity}%</p>
                    </div>
                )}
            </div>
        </>
    );
}
